// src/components/AdminScreen.js
import React, { useState } from 'react';

const AdminScreen = ({ companies, setCompanies }) => {
  const [editCompanyId, setEditCompanyId] = useState(null);
  const [editFormData, setEditFormData] = useState({ name: '', address: '' });

  // Handle Approve
  const handleApprove = (id) => {
    setCompanies(
      companies.map((company) =>
        company.id === id ? { ...company, status: 'APPROVED' } : company
      )
    );
  };

  // Handle Delete
  const handleDelete = (id) => {
    setCompanies(companies.filter((company) => company.id !== id));
  };

  // Handle Edit (open form with pre-filled data)
  const handleEditClick = (company) => {
    setEditCompanyId(company.id);
    setEditFormData({ name: company.name, address: company.address });
  };

  // Handle Edit form input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditFormData((prevData) => ({
      ...prevData, [name]: value,
    }));
  };

  // Save edited company
  const handleSaveEdit = (id) => {
    setCompanies(
      companies.map((company) =>
        company.id === id ? { ...company, ...editFormData } : company
      )
    );
    setEditCompanyId(null);
  };

  return (
    <div className="admin-container">
      <h2>Admin - Company List</h2>

      <table>
        <thead>
          <tr>
            <th>SNo</th>
            <th>Company Name</th>
            <th>Created By</th>
            <th>Company Address</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {companies.map((company, index) => (
            <tr key={company.id}>
              <td>{index + 1}</td>

              {/* If editing, show input fields. Otherwise, show normal text */}
              <td>
                {editCompanyId === company.id ? (
                  <input
                    type="text"
                    name="name"
                    value={editFormData.name}
                    onChange={handleInputChange}
                  />
                ) : (
                  company.name
                )}
              </td>

              <td>{company.created_by}</td>

              <td>
                {editCompanyId === company.id ? (
                  <input
                    type="text"
                    name="address"
                    value={editFormData.address}
                    onChange={handleInputChange}
                  />
                ) : (
                  company.address
                )}
              </td>

              <td>
                {editCompanyId === company.id ? (
                  <button onClick={() => handleSaveEdit(company.id)}>Save</button>
                ) : (
                  <button onClick={() => handleEditClick(company)}>Edit</button>
                )}
                <button onClick={() => handleDelete(company.id)}>Delete</button>

                {/* Approve button shown only for unapproved companies created by normal users */}
                {company.status === 'UNAPPROVED' && company.created_by !== 'admin' && (
                  <button onClick={() => handleApprove(company.id)}>Approve</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminScreen;
