// src/components/UserScreen.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css'
const UserScreen = () => {
  const [companies, setCompanies] = useState([]);
  const [newCompany, setNewCompany] = useState({ name: '', address: '' });

  useEffect(() => {
    axios.get('/api/my-companies')
      .then(response => setCompanies(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleCreateCompany = () => {
    axios.post('/api/companies', newCompany)
      .then(response => {
        setCompanies([...companies, response.data]);
      })
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h1>User - My Companies</h1>
      <ul>
        {companies.map(company => (
          <li key={company.id}>
            {company.name} - {company.status}
          </li>
        ))}
      </ul>
      <h2>Create New Company</h2>
      <input type="text" placeholder="Company Name" value={newCompany.name} onChange={(e) => setNewCompany({ ...newCompany, name: e.target.value })} />
      <input type="text" placeholder="Company Address" value={newCompany.address} onChange={(e) => setNewCompany({ ...newCompany, address: e.target.value })} />
      <button onClick={handleCreateCompany}>Create</button>
    </div>
  );
};

export default UserScreen;
