// src/components/AdminScreen.js
import React, { useState } from 'react';

const AdminScreen = ({ companies, setCompanies }) => {
  const [searchCreatedBy, setSearchCreatedBy] = useState('');
  const [searchCompanyName, setSearchCompanyName] = useState('');

  // Handle Approve
  const handleApprove = (id) => {
    setCompanies(
      companies.map((company) =>
        company.id === id ? { ...company, status: 'APPROVED' } : company
      )
    );
  };

  // Handle Delete
  const handleDelete = (id) => {
    setCompanies(companies.filter((company) => company.id !== id));
  };

  // Handle Edit (just simulating edit, for actual implementation, you need additional form)
  const handleEdit = (id, updatedCompany) => {
    setCompanies(
      companies.map((company) =>
        company.id === id ? { ...company, ...updatedCompany } : company
      )
    );
  };

  // Filter companies based on created_by or company name
  const filteredCompanies = companies.filter((company) => {
    return (
      (searchCreatedBy === '' || company.created_by.includes(searchCreatedBy)) &&
      (searchCompanyName === '' || company.name.includes(searchCompanyName))
    );
  });

  return (
    <div className="admin-container">
      <h2>Admin - Company List</h2>

      {/* Filters */}
      <div className="filters">
        <input
          type="text"
          placeholder="Search by Created By"
          value={searchCreatedBy}
          onChange={(e) => setSearchCreatedBy(e.target.value)}
        />
        <input
          type="text"
          placeholder="Search by Company Name"
          value={searchCompanyName}
          onChange={(e) => setSearchCompanyName(e.target.value)}
        />
      </div>

      {/* Company List */}
      <table>
        <thead>
          <tr>
            <th>SNo</th>
            <th>Company Name</th>
            <th>Created By</th>
            <th>Company Address</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredCompanies.map((company, index) => (
            <tr key={company.id}>
              <td>{index + 1}</td>
              <td>{company.name}</td>
              <td>{company.created_by}</td>
              <td>{company.address}</td>
              <td>
                <button onClick={() => handleEdit(company.id, { name: 'New Name' })}>
                  Edit
                </button>
                <button onClick={() => handleDelete(company.id)}>Delete</button>
                {company.status === 'UNAPPROVED' && (
                  <button onClick={() => handleApprove(company.id)}>Approve</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminScreen;
