// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import Register from './components/Register';
import Login from './components/Login';
import AdminScreen from './components/AdminScreen';
import UserScreen from './components/UserScreen';
import './App.css';

const App = () => {
  const [users, setUsers] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [loggedInUser, setLoggedInUser] = useState(null);

  return (
    <Router>
      <Switch>
        <Route path="/register">
          <Register setUsers={setUsers} users={users} />
        </Route>
        <Route path="/login">
          <Login users={users} setLoggedInUser={setLoggedInUser} />
        </Route>
        <Route path="/admin">
          <AdminScreen companies={companies} setCompanies={setCompanies} />
        </Route>
        <Route path="/user">
          <UserScreen companies={companies} setCompanies={setCompanies} loggedInUser={loggedInUser} />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
